mapper.select:
select free_idx, free_title, free_content, create_date
  from free
 where free_idx = #{free_idx}

mapper.update:
update free
   set free_title = #{free_title}, free_content = #{free_content}
 where free_idx = #{free_idx}

update free
   set f_del_yn = 'y'
 where free_idx = #{free_idx}
